  #{item}

mapper.select:
select d.case_id,d.type,c.title from (SELECT DISTINCT case_id, '6501' type FROM tp_case_listdetail
case_id in
#{item}
) d LEFT join tp_case_casedetail c on d.case_id = c.id

select c.id,c.case_number,c.product_id,p.product_name,c.function_id,f.function_name,c.level,c.img_path,c.precondition,c.title,c.exc_step,
c.Exp_results,c.remarks,c.status,c.apitest_caseid,c.unittest_caseid,c.uitest_caseid,c.backdev_id,c.relate_demand,c.frontdev_id,
c.productman_id,c.modified_time            ,c.modifier_id
from ((
select id,case_number,product_id,function_id,`level`,img_path,precondition,title,exc_step,Exp_results,
remarks,status,apitest_caseid,unittest_caseid,uitest_caseid,backdev_id,relate_demand,frontdev_id,productman_id,modified_time,modifier_id
from tp_case_casedetail

  and title like CONCAT('%',#{title},'%')
  and function_id = #{functionId}
  and product_id = #{productId}
  and status = #{status}
  and type = #{type}
) c
left join tp_case_product p on c.product_id = p.id)
left join  tp_case_function f on c.function_id = f.id
order by c.product_id,c.function_id
limit #{offset},#{limit}

select count(1)
from tp_case_casedetail


    and title = #{title}


    and function_id = #{functionId}


    and product_id = #{productId}


    and status = #{status}


    and `type` = #{type}


    and case_number = #{caseNumber}

mapper.update:
update tp_case_casedetail
set status = #{status}

    id in

      #{item}
