mapper.select:
select count(1)
from tp_case_function
where function_name = #{name}

mapper.update:
update tp_case_function

  set status = #{status}
  where FIND_IN_SET(id,getChiListById(#{ids},0))


  set status = #{status}
  where FIND_IN_SET(id,getChiListById(#{ids},1))

mapper.select:

